import React, { useEffect, useState } from 'react';
import { Container, Typography, Grid, Card, CardMedia, CardContent, Button, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Cars = () => {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/api/cars')
      .then((res) => res.json())
      .then((data) => {
        setCars(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching cars:', err);
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom align="center">
        All Cars
      </Typography>
      <Grid container spacing={4}>
        {cars.map((car) => (
          <Grid item key={car._id} xs={12} sm={6} md={4}>
            <Card sx={{ cursor: 'pointer' }} onClick={() => navigate(`/car/${car._id}`)}>
              <CardMedia
                component="img"
                height="200"
                image={`http://localhost:5000${car.images && car.images.length > 0 ? car.images[0] : ''}`}
                alt={car.title}
              />
              <CardContent>
                <Typography variant="h6">{car.title}</Typography>
                <Typography variant="body2" color="text.secondary">{car.brand} {car.model} ({car.year})</Typography>
                <Typography variant="body1" color="primary" sx={{ mt: 1 }}>
                  â‚¹{car.price.toLocaleString()}
                </Typography>
                <Typography variant="body2" color="text.secondary">{car.location}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Cars;
