import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Wishlist.css';

const Wishlist = ({ user }) => {
  const navigate = useNavigate();
  const [wishlistItems, setWishlistItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user) {
      fetch(`http://localhost:5000/api/wishlist/user/${user._id}`)
        .then(res => res.json())
        .then(data => {
          setWishlistItems(data);
          setLoading(false);
        })
        .catch(err => {
          console.error('Error fetching wishlist:', err);
          setLoading(false);
        });
    } else {
      setWishlistItems([]);
      setLoading(false);
    }
  }, [user]);

  const handleRemoveFromWishlist = (id) => {
    fetch(`http://localhost:5000/api/wishlist/${id}`, {
      method: 'DELETE',
    })
      .then(res => {
        if (res.status === 204) {
          setWishlistItems((prev) => prev.filter((item) => item._id !== id));
        }
      })
      .catch(err => console.error('Error removing from wishlist:', err));
  };

  const handleBookNow = () => {
    alert('Contact 9599942400 to Book Your Test Drive');
  };

  return (
    <div className="wishlist-container">
      <h1 className="wishlist-title">My Wishlist</h1>
      <div className="wishlist-list">
        {wishlistItems.length > 0 ? (
          <div className="wishlist-grid">
            {wishlistItems.map((item) => (
              <div className="wishlist-card" key={item._id}>
                <img
                  className="wishlist-img"
                  src={item.carId.images && item.carId.images[0] ? `http://localhost:5000${item.carId.images[0]}` : ''}
                  alt={item.carId.name}
                  onClick={() => navigate(`/car/${item.carId._id}`)}
                  style={{ cursor: 'pointer' }}
                />
                <div className="wishlist-card-content">
                  <h2 className="wishlist-car-title" onClick={() => navigate(`/car/${item.carId._id}`)} style={{ cursor: 'pointer' }}>
                    {item.carId.name || item.carId.title}
                  </h2>
                  <div className="wishlist-price">{item.carId.price}</div>
                  <div className="wishlist-details">
                    <div>Fuel Type: {item.carId.fuelType}</div>
                    <div>Transmission: {item.carId.transmission}</div>
                  </div>
                </div>
                <div className="wishlist-actions">
                  <button className="wishlist-btn wishlist-delete" onClick={() => handleRemoveFromWishlist(item._id)}>Delete</button>
                  <button className="wishlist-btn wishlist-book" onClick={handleBookNow}>Book Now</button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div >
            <p>Your wishlist is empty</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Wishlist;
