import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Typography,
  Box,
  Button,
  Paper,
  Chip,
  Rating,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from '@mui/material';
import { useParams } from 'react-router-dom';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import DirectionsCarIcon from '@mui/icons-material/DirectionsCar';
import LocalGasStationIcon from '@mui/icons-material/LocalGasStation';
import SpeedIcon from '@mui/icons-material/Speed';
import EventIcon from '@mui/icons-material/Event';

const CarDetails = () => {
  const { id } = useParams();
  const [carData, setCarData] = useState(null);
  const [isWishlisted, setIsWishlisted] = useState(false);
  const [openBooking, setOpenBooking] = useState(false);
  const [selectedImage, setSelectedImage] = useState(0);

  useEffect(() => {
    fetch(`http://localhost:5000/api/cars/${id}`)
      .then(res => res.json())
      .then(data => setCarData(data))
      .catch(err => console.error('Error fetching car details:', err));
  }, [id]);

  if (!carData) return <div>Loading...</div>;

  const handleBooking = () => {
    setOpenBooking(true);
  };

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      <Grid container spacing={4}>
        {/* Image Gallery */}
        <Grid item xs={12} md={7}>
          <Paper elevation={2} sx={{ p: 2, borderRadius: 2 }}>
            <Box sx={{ position: 'relative' }}>
              <img
                src={`http://localhost:5000${carData.images[selectedImage]}`}
                alt={carData.name}
                style={{ width: '100%', height: '400px', objectFit: 'cover', borderRadius: '8px' }}
              />
            </Box>
            <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
              {carData.images && carData.images.length > 0 && carData.images.map((image, index) => (
                <img
                  key={index}
                  src={`http://localhost:5000${image}`}
                  alt={`${carData.name} ${index + 1}`}
                  style={{
                    width: '100px',
                    height: '70px',
                    objectFit: 'cover',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    border: selectedImage === index ? '2px solid #ff4d4d' : 'none',
                  }}
                  onClick={() => setSelectedImage(index)}
                />
              ))}
            </Box>
          </Paper>
        </Grid>

        {/* Car Details */}
        <Grid item xs={12} md={5}>
          <Paper elevation={2} sx={{ p: 3, borderRadius: 2 }}>
            <Typography variant="h4" gutterBottom>
              {carData.name}
            </Typography>
            <Typography variant="h5" color="primary.main" gutterBottom>
              {carData.price}
            </Typography>

            <Grid container spacing={2} sx={{ mb: 3 }}>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <EventIcon />
                  <Typography>{carData.year}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <SpeedIcon />
                  <Typography>{carData.kmDriven} km</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <LocalGasStationIcon />
                  <Typography>{carData.fuelType}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <DirectionsCarIcon />
                  <Typography>{carData.transmission}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Typography>Owner Count:</Typography>
                  <Typography>{carData.ownerCount}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Typography>Registration No.:</Typography>
                  <Typography>{carData.registrationNumber}</Typography>
                </Box>
              </Grid>
            </Grid>

            <Typography variant="body1" paragraph>
              {carData.description}
            </Typography>

            <Button
              variant="contained"
              fullWidth
              size="large"
              onClick={handleBooking}
              sx={{ mb: 2 }}
            >
              Book Now
            </Button>
            <Button
              variant="outlined"
              fullWidth
              size="large"
              sx={{ mb: 2 }}
              onClick={() => {/* Add to wishlist logic here */}}
            >
              Add to Wishlist
            </Button>
          </Paper>
        </Grid>
      </Grid>

      {/* Booking Dialog */}
      <Dialog open={openBooking} onClose={() => setOpenBooking(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Book Your Test Drive</DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2 }}>
            <TextField
              fullWidth
              label="Preferred Date"
              type="date"
              InputLabelProps={{ shrink: true }}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="Preferred Time"
              type="time"
              InputLabelProps={{ shrink: true }}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="Additional Notes"
              multiline
              rows={4}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenBooking(false)}>Cancel</Button>
          <Button variant="contained" onClick={() => setOpenBooking(false)}>
            Confirm Booking
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default CarDetails;
