import React from 'react';
import { AppBar, Toolbar, Typography, Button, Box, Snackbar, Alert } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../store/slices/authSlice';

const Navbar = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const isAuthenticated = useSelector((state) => state.auth.isAuthenticated);
  const [open, setOpen] = React.useState(false);

  const handleLogout = () => {
    dispatch(logout());
    navigate('/login');
  };

  const handleProtectedClick = (e) => {
    if (!isAuthenticated) {
      e.preventDefault();
      setOpen(true);
    }
  };

  return (
    <AppBar position="static" color="primary">
      <Toolbar>
        <Typography variant="h6" component={Link} to="/" sx={{ flexGrow: 1, textDecoration: 'none', color: 'inherit', display: 'flex', alignItems: 'center' }}>
          <img src="/supermotor-logo.jpg" alt="SuperMotor Logo" style={{ height: 40, marginRight: 10 }} />
          SuperMotor
        </Typography>
        <Box>
          <Button color="inherit" component={Link} to="/buy" onClick={handleProtectedClick}>Buy</Button>
          <Button color="inherit" component={Link} to="/sell" onClick={handleProtectedClick}>Sell</Button>
          {!isAuthenticated ? (
            <Button color="inherit" component={Link} to="/login">Login/Signup</Button>
          ) : (
            <>
              <Button color="inherit" component={Link} to="/cars">Cars</Button>
              <Button color="inherit" onClick={handleLogout}>Logout</Button>
            </>
          )}
        </Box>
        <Snackbar open={open} autoHideDuration={3000} onClose={() => setOpen(false)} anchorOrigin={{ vertical: 'top', horizontal: 'center' }}>
          <Alert onClose={() => setOpen(false)} severity="info" sx={{ width: '100%' }}>
            Please login first to access this feature.
          </Alert>
        </Snackbar>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
