import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Signup.css';

const Signup = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    phone: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = {
      name: formData.firstName + ' ' + formData.lastName,
      email: formData.email,
      phone: formData.phone,
      password: formData.password,
    };
    try {
      const response = await fetch('http://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });
      if (response.ok) {
        alert('Signup successful! Please login.');
        navigate('/login');
      } else {
        const errorData = await response.json();
        alert('Signup failed: ' + (errorData.message || (errorData.errors && errorData.errors[0]?.msg) || 'Unknown error'));
      }
    } catch (error) {
      alert('Signup failed: ' + error.message);
    }
  };

  return (
    <div className="signup-container">
      <form className="signup-form" onSubmit={handleSubmit}>
        <div className="signup-title">Create Account</div>
        <div style={{ marginBottom: '1rem', color: '#666', textAlign: 'center' }}>
          Join us to start your car buying journey
        </div>
        <input
          className="signup-input"
          type="text"
          name="firstName"
          placeholder="First Name"
          value={formData.firstName}
          onChange={handleChange}

        />
        <input
          className="signup-input"
          type="text"
          name="lastName"
          placeholder="Last Name"
          value={formData.lastName}
          onChange={handleChange}

        />
        <input
          className="signup-input"
          type="email"
          name="email"
          placeholder="Email Address"
          value={formData.email}
          onChange={handleChange}

        />
        <input
          className="signup-input"
          type="tel"
          name="phone"
          placeholder="Phone Number"
          value={formData.phone}
          onChange={handleChange}

        />
        <input
          className="signup-input"
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}

        />
        <button className="signup-btn" type="submit">
          Sign Up
        </button>
        <div style={{ textAlign: 'center', marginTop: '1.5rem', color: '#666' }}>
          Already have an account?{' '}
          <button
            type="button"
            style={{ background: 'none', border: 'none', color: '#0a2a66', fontWeight: 600, cursor: 'pointer', padding: 0 }}
            onClick={() => navigate('/login')}
          >
            Sign in
          </button>
        </div>
      </form>
    </div>
  );
};

export default Signup;
