import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Typography,
  Box,
  Button,
  Paper,
  Chip,
  Rating,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from '@mui/material';
import { useParams } from 'react-router-dom';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import DirectionsCarIcon from '@mui/icons-material/DirectionsCar';
import LocalGasStationIcon from '@mui/icons-material/LocalGasStation';
import SpeedIcon from '@mui/icons-material/Speed';
import EventIcon from '@mui/icons-material/Event';
import { useSelector } from 'react-redux';
import Snackbar from '@mui/material/Snackbar';

const CarDetails = () => {
  const { id } = useParams();
  const [carData, setCarData] = useState(null);
  const [isWishlisted, setIsWishlisted] = useState(false);
  const [openBooking, setOpenBooking] = useState(false);
  const [selectedImage, setSelectedImage] = useState(0);
  const user = useSelector((state) => state.auth.user);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  useEffect(() => {
    fetch(`http://localhost:5000/api/cars/${id}`)
      .then(res => res.json())
      .then(data => setCarData(data))
      .catch(err => console.error('Error fetching car details:', err));
  }, [id]);

  if (!carData) return <div>Loading...</div>;

  const handleBooking = () => {
    setOpenBooking(true);
  };

  const handleAddToWishlist = async () => {
    if (!user) {
      setSnackbar({ open: true, message: 'Please log in to add to wishlist.', severity: 'error' });
      return;
    }
    try {
      const res = await fetch('http://localhost:5000/api/wishlist', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: user._id, carId: carData._id })
      });
      if (res.ok) {
        setSnackbar({ open: true, message: 'Added to wishlist!', severity: 'success' });
        setIsWishlisted(true);
      } else {
        const data = await res.json();
        setSnackbar({ open: true, message: data.error || 'Failed to add to wishlist.', severity: 'error' });
      }
    } catch (err) {
      setSnackbar({ open: true, message: 'Network error.', severity: 'error' });
    }
  };

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      <Grid container spacing={4}>
        {/* Image Gallery */}
        <Grid item xs={12} md={7}>
          <Paper elevation={2} sx={{ p: 2, borderRadius: 2 }}>
            <Box sx={{ position: 'relative' }}>
              <img
                src={`http://localhost:5000${carData.images[selectedImage]}`}
                alt={carData.name}
                style={{ width: '100%', height: '400px', objectFit: 'cover', borderRadius: '8px' }}
              />
            </Box>
            <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
              {carData.images && carData.images.length > 0 && carData.images.map((image, index) => (
                <img
                  key={index}
                  src={`http://localhost:5000${image}`}
                  alt={`${carData.name} ${index + 1}`}
                  style={{
                    width: '100px',
                    height: '70px',
                    objectFit: 'cover',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    border: selectedImage === index ? '2px solid #ff4d4d' : 'none',
                  }}
                  onClick={() => setSelectedImage(index)}
                />
              ))}
            </Box>
          </Paper>
        </Grid>

        {/* Car Details */}
        <Grid item xs={12} md={5}>
          <Paper elevation={2} sx={{ p: 3, borderRadius: 2 }}>
            <Typography variant="h4" gutterBottom>
              {carData.name}
            </Typography>
            <Typography variant="h5" color="primary.main" gutterBottom>
              {carData.price}
            </Typography>

            <Grid container spacing={2} sx={{ mb: 3 }}>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <EventIcon />
                  <Typography>{carData.year}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, justifyContent: 'flex-end' }}>
                  <Typography sx={{ fontWeight: 500 }}>Kilometers Driven:</Typography>
                  <Typography sx={{ ml: 1 }}>{carData.kmsDriven} km</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <LocalGasStationIcon />
                  <Typography>{carData.fuelType}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, justifyContent: 'flex-end' }}>
                  <DirectionsCarIcon />
                  <Typography>{carData.transmission}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Typography sx={{ fontWeight: 500 }}>Owner Count:</Typography>
                  <Typography sx={{ ml: 1 }}>{carData.ownerCount}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, justifyContent: 'flex-end' }}>
                  <Typography sx={{ fontWeight: 500 }}>Registration No.:</Typography>
                  <Typography sx={{ ml: 1 }}>{carData.registrationNumber}</Typography>
                </Box>
              </Grid>
            </Grid>

            <Typography variant="body1" paragraph>
              {carData.description}
            </Typography>

            <Button
              variant="contained"
              fullWidth
              size="large"
              onClick={handleBooking}
              sx={{ mb: 2 }}
            >
              Book Now
            </Button>
            <Button
              variant="outlined"
              fullWidth
              size="large"
              sx={{ mb: 2 }}
              onClick={handleAddToWishlist}
              disabled={isWishlisted}
            >
              {isWishlisted ? 'Wishlisted' : 'Add to Wishlist'}
            </Button>
          </Paper>
        </Grid>
      </Grid>

      {/* Booking Dialog */}
      <Dialog open={openBooking} onClose={() => setOpenBooking(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Book Your Test Drive</DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2 }}>
            <TextField
              fullWidth
              label="Preferred Date"
              type="date"
              InputLabelProps={{ shrink: true }}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="Preferred Time"
              type="time"
              InputLabelProps={{ shrink: true }}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="Additional Notes"
              multiline
              rows={4}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenBooking(false)}>Cancel</Button>
          <Button variant="contained" onClick={() => setOpenBooking(false)}>
            Confirm Booking
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        message={snackbar.message}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      />
    </Container>
  );
};

export default CarDetails;
