import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Button,
  Box,
  Rating,
  IconButton,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
import DirectionsCarIcon from '@mui/icons-material/DirectionsCar';
import LocalGasStationIcon from '@mui/icons-material/LocalGasStation';
import SpeedIcon from '@mui/icons-material/Speed';
import { useSelector } from 'react-redux';

const Wishlist = () => {
  const navigate = useNavigate();
  const user = useSelector((state) => state.auth.user);
  const [wishlistItems, setWishlistItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user) {
      fetch(`http://localhost:5000/api/wishlist/user/${user._id}`)
        .then(res => res.json())
        .then(data => {
          setWishlistItems(data);
          setLoading(false);
        })
        .catch(err => {
          console.error('Error fetching wishlist:', err);
          setLoading(false);
        });
    } else {
      setWishlistItems([]);
      setLoading(false);
    }
  }, [user]);

  const handleRemoveFromWishlist = (id) => {
    fetch(`http://localhost:5000/api/wishlist/${id}`, {
      method: 'DELETE',
    })
      .then(res => {
        if (res.status === 204) {
          setWishlistItems((prev) => prev.filter((item) => item._id !== id));
        }
      })
      .catch(err => console.error('Error removing from wishlist:', err));
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom>
        My Wishlist
      </Typography>

      <Grid container spacing={3}>
        {loading ? (
          <Typography variant="body1">Loading...</Typography>
        ) : wishlistItems.length > 0 ? (
          wishlistItems.map((item) => (
            <Grid item xs={12} sm={6} md={4} key={item._id}>
              <Card
                sx={{
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  position: 'relative',
                  '&:hover': { transform: 'translateY(-4px)', transition: 'transform 0.2s' },
                }}
              >
                <IconButton
                  size="small"
                  onClick={() => handleRemoveFromWishlist(item._id)}
                  sx={{
                    position: 'absolute',
                    top: 8,
                    right: 8,
                    backgroundColor: 'white',
                    '&:hover': { backgroundColor: 'white', color: 'primary.main' },
                  }}
                >
                  <DeleteOutlineIcon />
                </IconButton>

                <CardMedia
                  component="img"
                  height="200"
                  image={item.carId.images && item.carId.images[0] ? `http://localhost:5000${item.carId.images[0]}` : ''}
                  alt={item.carId.name}
                  sx={{ objectFit: 'cover' }}
                />

                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography gutterBottom variant="h6" component="h2">
                    {item.carId.name || item.carId.title}
                  </Typography>

                  <Typography variant="h6" color="primary.main" gutterBottom>
                    {item.carId.price}
                  </Typography>

                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                    <Rating value={item.carId.rating} precision={0.5} readOnly size="small" />
                    <Typography variant="body2" color="text.secondary" sx={{ ml: 1 }}>
                      ({item.carId.rating})
                    </Typography>
                  </Box>

                  <Grid container spacing={1}>
                    <Grid item xs={6}>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <SpeedIcon fontSize="small" color="action" />
                        <Typography variant="body2" color="text.secondary">
                          {item.carId.mileage}
                        </Typography>
                      </Box>
                    </Grid>
                    <Grid item xs={6}>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <LocalGasStationIcon fontSize="small" color="action" />
                        <Typography variant="body2" color="text.secondary">
                          {item.carId.fuelType}
                        </Typography>
                      </Box>
                    </Grid>
                    <Grid item xs={12}>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <DirectionsCarIcon fontSize="small" color="action" />
                        <Typography variant="body2" color="text.secondary">
                          {item.carId.transmission}
                        </Typography>
                      </Box>
                    </Grid>
                  </Grid>
                </CardContent>

                <CardActions sx={{ p: 2, pt: 0 }}>
                  <Button
                    size="small"
                    variant="outlined"
                    fullWidth
                    onClick={() => navigate(`/car/${item.carId._id}`)}
                    sx={{ mr: 1 }}
                  >
                    View Details
                  </Button>
                  <Button
                    size="small"
                    variant="contained"
                    fullWidth
                    onClick={() => navigate(`/car/${item.carId._id}`)}
                  >
                    Book Now
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))
        ) : (
          <Box
            sx={{
              textAlign: 'center',
              py: 8,
            }}
          >
            <Typography variant="h6" color="text.secondary" gutterBottom>
              Your wishlist is empty
            </Typography>
            <Button
              variant="contained"
              onClick={() => navigate('/')}
              sx={{ mt: 2 }}
            >
              Browse Cars
            </Button>
          </Box>
        )}
      </Grid>
    </Container>
  );
};

export default Wishlist;
