import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Wishlist.css';

const Wishlist = ({ user }) => {
  const [wishlistItems, setWishlistItems] = useState([]);
  const [message, setMessage] = useState('');
  const [showMessage, setShowMessage] = useState(false);


  useEffect(() => {
    if (user) {
      fetch(`http://localhost:5000/api/wishlist/user/${user._id}`)
        .then(res => res.json())
        .then(data => {
          setWishlistItems(data);

        })
        .catch(err => {
          console.error('Error fetching wishlist:', err);

        });
    } else {
      setWishlistItems([]);
    }
  }, [user]);

  const handleRemoveFromWishlist = (id) => {
    fetch(`http://localhost:5000/api/wishlist/${id}`, {
      method: 'DELETE',
    })
      .then(res => {
        if (res.status === 204) {
          setWishlistItems((prev) => prev.filter((item) => item._id !== id));
        }
      })
      .catch(err => console.error('Error removing from wishlist:', err));
  };

  const handleBookNow = () => {
    setMessage(<b>Contact 9599942400 to Book Your Test Drive.</b>);
    setShowMessage(true);
    setTimeout(() => setShowMessage(false), 3000);
  };

  return (
    <div className="container">
      {showMessage && (
        <div className="custom-message-box">
          {message}
        </div>
      )}
      <h1 className="title">Shortlisted Cars</h1>
      <div className="list">
        {wishlistItems.length > 0 ? (
          <div className="grid">
            {wishlistItems.map((item) => (
              <div className="card" key={item._id}>
                <Link to={`/car/${item.carId._id}`}>
                  <img
                    className="image"
                    src={item.carId.images && item.carId.images[0] ? `http://localhost:5000${item.carId.images[0]}` : ''}
                    alt={item.carId.name}
                    style={{ cursor: 'pointer' }}
                  />
                </Link>
                <div className="card-content">
                  <Link to={`/car/${item.carId._id}`} className="car-title" style={{ cursor: 'pointer' }}>
                    {item.carId.model}
                  </Link>
                  <div className="price">â‚¹{item.carId.price.toLocaleString('en-IN')}</div>
                  <div className="details">
                    <div>Fuel Type: {item.carId.fuelType}</div>
                    <div>Transmission: {item.carId.transmission}</div>
                  </div>
                </div>
                <div className="action">
                  <button onClick={() => handleRemoveFromWishlist(item._id)}>Delete</button>
                  <button onClick={handleBookNow}>Book Now</button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div >
            <p>Your wishlist is empty</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Wishlist;
