import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const navigate = useNavigate();
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/api/cars')
      .then((res) => res.json())
      .then((data) => {
        setCars(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching cars:', err);
        setLoading(false);
      });
  }, []);

  return (
    <div>
      {/* Hero Section */}
      <div className="home-hero">
        <div className="container">
          <div className="hero-grid">
            <div className="hero-text">
              <h1>Your Trusted & Hassle Free Car Buying Partner</h1>
              <p>
                Get the best deals without the hassle of dealerships. Personalized service,
                better delivery, and affordable financing.
              </p>
              <button
                className="primary-btn"
                onClick={() => navigate('/signup')}
              >
                Find Your Car
              </button>
            </div>
            <div className="hero-image">
              <img
                src="/LAM.jpg"
                alt="Luxury Car"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Why Choose Us Section */}
      <div className="container">
        <div className="why-section">
          <h2 className="section-title">Why Choose SUPERMOTOR</h2>
          <div className="why-grid">
            <div className="why-box">
              <h3>Expert Guidance</h3>
              <p>
                Our team of experts will help you find the perfect car that matches your needs and budget.
              </p>
            </div>
            <div className="why-box">
              <h3>Best Deals</h3>
              <p>
                Get access to exclusive deals and competitive pricing on our wide selection of vehicles.
              </p>
            </div>
            <div className="why-box">
              <h3>Easy Financing</h3>
              <p>
                Quick and hassle-free financing options tailored to your requirements.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
