import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Box,
  Paper,
  Grid,
  Chip,
  Button,
  Stepper,
  Step,
  StepLabel,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import ReceiptIcon from '@mui/icons-material/Receipt';
import LocalShippingIcon from '@mui/icons-material/LocalShipping';
import PaymentIcon from '@mui/icons-material/Payment';
import { useSelector } from 'react-redux';
import './Orders.css';

const Orders = () => {
  const navigate = useNavigate();
  const [soldCars, setSoldCars] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/api/cars/sold')
      .then(res => res.json())
      .then(data => {
        setSoldCars(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching sold cars:', err);
        setLoading(false);
      });
  }, []);

  const getStatusColor = (status) => {
    switch (status) {
      case 'Processing':
        return 'warning';
      case 'Confirmed':
        return 'info';
      case 'Delivered':
        return 'success';
      case 'sold':
        return 'success';
      default:
        return 'default';
    }
  };

  const orderSteps = ['Order Placed', 'Processing', 'Delivered'];

  return (
    <Container maxWidth="lg" className="orders-container">
      <Typography variant="h4" gutterBottom>
        My Orders
      </Typography>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Box sx={{ textAlign: 'center', py: 8 }}>
            <Typography variant="h6" color="text.secondary" gutterBottom>
              No cars bought.
            </Typography>
            <Button
              variant="contained"
              onClick={() => navigate('/')}
              sx={{ mt: 2 }}
            >
              Browse Cars
            </Button>
          </Box>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Orders;
