import React from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [open, setOpen] = React.useState(false);
  const isAuthenticated = !!localStorage.getItem('user');

  const isCarsPage = location.pathname.startsWith('/cars');

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleProtectedClick = (e) => {
    if (!isAuthenticated) {
      e.preventDefault();
      setOpen(true);
      setTimeout(() => setOpen(false), 3000);
    }
  };

  return (
    <nav className="navbar">
      <div className="navbar-inner">
        <div className="navbar-logo-box">
          <Link to="/">
            <img src="/supermotor-logo.jpg" alt="SuperMotor Logo" className="navbar-logo" style={{ cursor: 'pointer' }} />
          </Link>
        </div>
        <div className="navbar-links">
          {isAuthenticated ? (
            <>
              <Link className="navbar-link" to="/">Home</Link>
              <Link className="navbar-link" to="/cars">Buy</Link>
              <Link className="navbar-link" to="/cars">Sell</Link>
              <Link className="navbar-link" to="/bookings">Bookings</Link>
              <Link className="navbar-link" to="/wishlist">Wishlist</Link>
              <Link className="navbar-link" to="/orders">Orders</Link>
              <Link className="navbar-link" to="/account">Account</Link>
              <button className="navbar-link navbar-logout" onClick={handleLogout}>Logout</button>
            </>
          ) : (
            <>
              <Link className="navbar-link" to="/buy" onClick={handleProtectedClick}>Buy</Link>
              <Link className="navbar-link" to="/sell" onClick={handleProtectedClick}>Sell</Link>
              <Link className="navbar-link" to="/login">Login/SignUp</Link>
            </>
          )}
        </div>
      </div>
      {open && (
        <div className="navbar-alert">
          Please login first to access this feature.
        </div>
      )}
    </nav>
  );
};

export default Navbar;
