import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Typography,
  Box,
  Button,
  Paper,
  Chip,
  Rating,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from '@mui/material';
import { useParams } from 'react-router-dom';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import DirectionsCarIcon from '@mui/icons-material/DirectionsCar';
import LocalGasStationIcon from '@mui/icons-material/LocalGasStation';
import SpeedIcon from '@mui/icons-material/Speed';
import EventIcon from '@mui/icons-material/Event';
import { useSelector } from 'react-redux';
import Snackbar from '@mui/material/Snackbar';
import './CarDetails.css';

const CarDetails = () => {
  const { id } = useParams();
  const [carData, setCarData] = useState(null);
  const [isWishlisted, setIsWishlisted] = useState(false);
  const [selectedImage, setSelectedImage] = useState(0);
  const user = useSelector((state) => state.auth.user);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  useEffect(() => {
    fetch(`http://localhost:5000/api/cars/${id}`)
      .then(res => res.json())
      .then(data => setCarData(data))
      .catch(err => console.error('Error fetching car details:', err));
  }, [id]);

  if (!carData) return <div>Loading...</div>;

  const handleBookNow = () => {
    alert('Contact 9599942400 to book your test drive');
  };

  const handleAddToWishlist = async () => {
    if (!user) {
      setSnackbar({ open: true, message: 'Please log in to add to wishlist.', severity: 'error' });
      return;
    }
    try {
      const res = await fetch('http://localhost:5000/api/wishlist', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: user._id, carId: carData._id })
      });
      if (res.ok) {
        setSnackbar({ open: true, message: 'Added to wishlist!', severity: 'success' });
        setIsWishlisted(true);
      } else {
        const data = await res.json();
        setSnackbar({ open: true, message: data.error || 'Failed to add to wishlist.', severity: 'error' });
      }
    } catch (err) {
      setSnackbar({ open: true, message: 'Network error.', severity: 'error' });
    }
  };

  return (
    <Container maxWidth="xl" className="car-details-container">
      <Grid container spacing={4}>
        {/* Image Gallery */}
        <Grid item xs={12} md={7}>
          <Paper elevation={2} className="car-details-paper">
            <Box sx={{ position: 'relative' }}>
              <img
                src={`http://localhost:5000${carData.images[selectedImage]}`}
                alt={carData.name}
                className="car-details-img-main"
              />
            </Box>
            <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
              {carData.images && carData.images.length > 0 && carData.images.map((image, index) => (
                <img
                  key={index}
                  src={`http://localhost:5000${image}`}
                  alt={`${carData.name} ${index + 1}`}
                  className={`car-details-img-thumb${selectedImage === index ? ' selected' : ''}`}
                  onClick={() => setSelectedImage(index)}
                />
              ))}
            </Box>
          </Paper>
        </Grid>

        {/* Car Details */}
        <Grid item xs={12} md={5}>
          <Paper elevation={2} className="car-details-paper">
            <Typography variant="h4" gutterBottom className="car-details-title">
              {carData.name}
            </Typography>
            <Typography variant="h5" color="primary.main" gutterBottom className="car-details-price">
              {carData.price}
            </Typography>

            <Grid container spacing={2} className="car-details-section">
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <EventIcon />
                  <Typography>{carData.year}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Typography sx={{ fontWeight: 500 }}>Kilometers Driven:</Typography>
                  <Typography>{carData.kmsDriven} km</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <LocalGasStationIcon />
                  <Typography>{carData.fuelType}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <DirectionsCarIcon />
                  <Typography>{carData.transmission}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Typography sx={{ fontWeight: 500 }}>Owner Count:</Typography>
                  <Typography>{carData.ownerCount}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Typography sx={{ fontWeight: 500 }}>Registration No.:</Typography>
                  <Typography>{carData.registrationNumber}</Typography>
                </Box>
              </Grid>
            </Grid>

            <Typography variant="body1" paragraph>
              {carData.description}
            </Typography>

            <Button
              variant="contained"
              fullWidth
              size="large"
              onClick={handleBookNow}
              className="car-details-btn"
              sx={{ mb: 2 }}
            >
              Book Now
            </Button>
            <Button
              variant="outlined"
              fullWidth
              size="large"
              className="car-details-btn"
              sx={{ mb: 2 }}
              onClick={handleAddToWishlist}
              disabled={isWishlisted}
            >
              {isWishlisted ? 'Wishlisted' : 'Add to Wishlist'}
            </Button>
          </Paper>
        </Grid>
      </Grid>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        message={snackbar.message}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      />
    </Container>
  );
};

export default CarDetails;
