import React from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  Snackbar,
  Alert,
} from '@mui/material';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../store/slices/authSlice';

const Navbar = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const location = useLocation();

  const isAuthenticated = useSelector((state) => !!state.auth.user);
  const [open, setOpen] = React.useState(false);

  const isCarsPage = location.pathname.startsWith('/cars');
  console.log('PATH:', location.pathname, 'isCarsPage:', isCarsPage, 'isAuthenticated:', isAuthenticated);

  const handleLogout = () => {
    dispatch(logout());
    navigate('/login');
  };

  const handleProtectedClick = (e) => {
    if (!isAuthenticated) {
      e.preventDefault();
      setOpen(true);
    }
  };

  return (
    <AppBar position="static" sx={{ backgroundColor: 'white', color: 'black' }} elevation={1}>
      <Toolbar>
        <Box sx={{ flexGrow: 1, display: 'flex', alignItems: 'center' }}>
          <img src="/supermotor-logo.jpg" alt="SuperMotor Logo" style={{ height: 40 }} />
        </Box>

        <Box>
          {/* Show protected buttons if authenticated */}
          {isAuthenticated ? (
            <>
              <Button color="inherit" component={Link} to="/cars">Cars</Button>
              <Button color="inherit" component={Link} to="/bookings">Bookings</Button>
              <Button color="inherit" component={Link} to="/wishlist">Wishlist</Button>
              <Button color="inherit" component={Link} to="/orders">Orders</Button>
              <Button color="inherit" component={Link} to="/account">Account</Button>
              <Button color="inherit" onClick={handleLogout}>Logout</Button>
            </>
          ) : (
            <>
              <Button color="inherit" component={Link} to="/buy" onClick={handleProtectedClick}>Buy</Button>
              <Button color="inherit" component={Link} to="/sell" onClick={handleProtectedClick}>Sell</Button>
              <Button color="inherit" component={Link} to="/login">Login/Signup</Button>
            </>
          )}
        </Box>

        <Snackbar
          open={open}
          autoHideDuration={3000}
          onClose={() => setOpen(false)}
          anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
        >
          <Alert onClose={() => setOpen(false)} severity="info" sx={{ width: '100%' }}>
            Please login first to access this feature.
          </Alert>
        </Snackbar>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
