import React, { useState } from 'react';
import './Sell.css';

const Sell = () => {
  const [form, setForm] = useState({
    brand: '',
    model: '',
    year: '',
    price: ''
  });
  const [message, setMessage] = useState('');
  const [showMessage, setShowMessage] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const date = form.date;
    const time = form.time;
    if (date && time) {
      setMessage(
        <b>Your Inspection has been booked for {date} at {time}.</b>
      );
      setShowMessage(true);
      setTimeout(() => setShowMessage(false), 3500);
      form.reset();
    }
  };

  return (
    <div className="container ">
      <div className="sell">
      {showMessage && (
        <div className="custom-message-box">{message}</div>
      )}
      <h1>Sell Your Car</h1>
      <form onSubmit={handleSubmit}>
        <h1>Book Inspection</h1>
        <input type="text" placeholder="Name" name="name" />
        <input type="email" placeholder="Email" name="email" />
        <input type="phone" placeholder="Mobile Number" name="phone" />
        <input type="text" placeholder="Address" name="address" />
        <input type="text" placeholder="Brand Name" name="brand" />
        <input type="text" placeholder="Car Model" name="model" />
        <input type="text" placeholder="Car Year" name="year" />
        {/* Transmission dropdown */}
        <select defaultValue="" required name="transmission">
          <option value="" disabled>Transmission</option>
          <option value="Manual">Manual</option>
          <option value="Automatic">Automatic</option>
          <option value="CVT">CVT</option>
          <option value="DCT">DCT</option>
          <option value="AMT">AMT</option>
          <option value="IMT">IMT (Intelligent Manual Transmission)</option>
        </select>
        <input type="text" placeholder="Registration No." name="registration" />
        <input type="date" placeholder="Date" name="date" />
        <input type="time" placeholder="Time" name="time" />
        <button type="submit">Book</button>
      </form>
    </div>
    </div>
  );
};
export default Sell;
