import React, { useState } from 'react';
import {
  Container,
  Grid,
  Typography,
  Box,
  Button,
  Paper,
  Chip,
  Rating,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from '@mui/material';
import { useParams } from 'react-router-dom';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import DirectionsCarIcon from '@mui/icons-material/DirectionsCar';
import LocalGasStationIcon from '@mui/icons-material/LocalGasStation';
import SpeedIcon from '@mui/icons-material/Speed';
import EventIcon from '@mui/icons-material/Event';

const CarDetails = () => {
  const { id } = useParams();
  const [isWishlisted, setIsWishlisted] = useState(false);
  const [openBooking, setOpenBooking] = useState(false);
  const [selectedImage, setSelectedImage] = useState(0);

  // Mock car data - in real app, this would be fetched from an API
  const carData = {
    name: 'BMW 4 Series Gran Coupé',
    price: '$28,700',
    rating: 4.5,
    year: 2023,
    mileage: '10,000 mi',
    fuelType: 'Petrol',
    transmission: 'Automatic',
    images: [
      'https://images.unsplash.com/photo-1555215695-3004980ad54e?auto=format&fit=crop&w=800',
      'https://images.unsplash.com/photo-1617531653332-bd46c24f2068?auto=format&fit=crop&w=800',
      'https://images.unsplash.com/photo-1614200187524-dc4b892acf16?auto=format&fit=crop&w=800',
    ],
    description: 'Experience luxury and performance with the all-new BMW 4 Series Gran Coupé. This stunning vehicle combines elegant design with powerful performance.',
    features: [
      'Premium Leather Seats',
      'Panoramic Sunroof',
      'Navigation System',
      'Wireless Charging',
      'Parking Assistant',
      'LED Headlights',
    ],
  };

  const handleBooking = () => {
    setOpenBooking(true);
  };

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      <Grid container spacing={4}>
        {/* Image Gallery */}
        <Grid item xs={12} md={7}>
          <Paper elevation={2} sx={{ p: 2, borderRadius: 2 }}>
            <Box sx={{ position: 'relative' }}>
              <img
                src={carData.images[selectedImage]}
                alt={carData.name}
                style={{ width: '100%', height: '400px', objectFit: 'cover', borderRadius: '8px' }}
              />
              <IconButton
                sx={{
                  position: 'absolute',
                  top: 16,
                  right: 16,
                  backgroundColor: 'white',
                  '&:hover': { backgroundColor: 'white' },
                }}
                onClick={() => setIsWishlisted(!isWishlisted)}
              >
                {isWishlisted ? (
                  <FavoriteIcon color="primary" />
                ) : (
                  <FavoriteBorderIcon />
                )}
              </IconButton>
            </Box>
            <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
              {carData.images.map((image, index) => (
                <img
                  key={index}
                  src={image}
                  alt={`${carData.name} ${index + 1}`}
                  style={{
                    width: '100px',
                    height: '70px',
                    objectFit: 'cover',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    border: selectedImage === index ? '2px solid #ff4d4d' : 'none',
                  }}
                  onClick={() => setSelectedImage(index)}
                />
              ))}
            </Box>
          </Paper>
        </Grid>

        {/* Car Details */}
        <Grid item xs={12} md={5}>
          <Paper elevation={2} sx={{ p: 3, borderRadius: 2 }}>
            <Typography variant="h4" gutterBottom>
              {carData.name}
            </Typography>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
              <Rating value={carData.rating} precision={0.5} readOnly />
              <Typography variant="body2" color="text.secondary">
                (4.5/5)
              </Typography>
            </Box>
            <Typography variant="h5" color="primary.main" gutterBottom>
              {carData.price}
            </Typography>

            <Grid container spacing={2} sx={{ mb: 3 }}>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <EventIcon />
                  <Typography>{carData.year}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <SpeedIcon />
                  <Typography>{carData.mileage}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <LocalGasStationIcon />
                  <Typography>{carData.fuelType}</Typography>
                </Box>
              </Grid>
              <Grid item xs={6}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <DirectionsCarIcon />
                  <Typography>{carData.transmission}</Typography>
                </Box>
              </Grid>
            </Grid>

            <Typography variant="body1" paragraph>
              {carData.description}
            </Typography>

            <Typography variant="h6" gutterBottom>
              Key Features
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 3 }}>
              {carData.features.map((feature, index) => (
                <Chip key={index} label={feature} />
              ))}
            </Box>

            <Button
              variant="contained"
              fullWidth
              size="large"
              onClick={handleBooking}
              sx={{ mb: 2 }}
            >
              Book Now
            </Button>
          </Paper>
        </Grid>
      </Grid>

      {/* Booking Dialog */}
      <Dialog open={openBooking} onClose={() => setOpenBooking(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Book Your Test Drive</DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2 }}>
            <TextField
              fullWidth
              label="Preferred Date"
              type="date"
              InputLabelProps={{ shrink: true }}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="Preferred Time"
              type="time"
              InputLabelProps={{ shrink: true }}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="Additional Notes"
              multiline
              rows={4}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenBooking(false)}>Cancel</Button>
          <Button variant="contained" onClick={() => setOpenBooking(false)}>
            Confirm Booking
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default CarDetails;
