import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';
import './Cars.css';

const Cars = () => {
  const navigate = useNavigate();
  
  // State management
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    brand: '',
    carType: '',
    priceRange: [0, 100000000]
  });
  const [priceBounds, setPriceBounds] = useState({
    min: 0,
    max: 100000000
  });

  // Fetch cars data
  useEffect(() => {
    const fetchCars = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/cars');
        const data = await response.json();
        
        setCars(data);
        
        // Calculate price bounds
        const prices = data.map(car => car.price);
        const minPrice = Math.min(...prices);
        const maxPrice = Math.max(...prices);
        
        setPriceBounds({ min: minPrice, max: maxPrice });
        setFilters(prev => ({
          ...prev,
          priceRange: [minPrice, maxPrice]
        }));
      } catch (error) {
        console.error('Error fetching cars:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCars();
  }, []);

  // Get unique brands and car types
  const brands = [...new Set(cars.map(car => car.brand))].filter(Boolean);
  const carTypes = [...new Set(cars.map(car => car.carType))].filter(Boolean);

  // Filter cars based on current filters
  const filteredCars = cars.filter(car => {
    const matchesBrand = !filters.brand || car.brand === filters.brand;
    const matchesType = !filters.carType || car.carType === filters.carType;
    const matchesPrice = car.price >= filters.priceRange[0] && car.price <= filters.priceRange[1];
    
    return matchesBrand && matchesType && matchesPrice;
  });

  // Handle filter changes
  const handleFilterChange = (filterType, value) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: value
    }));
  };

  // Handle price range change
  const handlePriceRangeChange = (newRange) => {
    setFilters(prev => ({
      ...prev,
      priceRange: newRange
    }));
  };

  // Format price for display
  const formatPrice = (price) => {
    return Number.isFinite(price) ? price.toLocaleString() : '';
  };

  // Render filter sidebar
  const renderFilterSidebar = () => (
    <div className="cars-filter-sidebar">
      <h2 className="cars-filter-title">Filters</h2>
      
      {/* Brand Filter */}
      <div className="cars-form-group">
        <label htmlFor="brand-select">Brand</label>
        <select
          id="brand-select"
          value={filters.brand}
          onChange={(e) => handleFilterChange('brand', e.target.value)}
          className="cars-select"
        >
          <option value="">All</option>
          {brands.map(brand => (
            <option key={brand} value={brand}>{brand}</option>
          ))}
        </select>
      </div>

      {/* Car Type Filter */}
      <div className="cars-form-group">
        <label htmlFor="type-select">Car Type</label>
        <select
          id="type-select"
          value={filters.carType}
          onChange={(e) => handleFilterChange('carType', e.target.value)}
          className="cars-select"
        >
          <option value="">All</option>
          {carTypes.map(type => (
            <option key={type} value={type}>{type}</option>
          ))}
        </select>
      </div>

      {/* Price Range Filter */}
      <div className="cars-form-group cars-price-range">
        <label>Price Range (₹)</label>
        <div className="cars-range-labels">
          <span>{formatPrice(filters.priceRange[0])}</span>
          <span>to</span>
          <span>{formatPrice(filters.priceRange[1])}</span>
        </div>
        <Slider
          min={priceBounds.min}
          max={priceBounds.max}
          value={filters.priceRange}
          onChange={handlePriceRangeChange}
          step={10000}
          allowCross={false}
          trackStyle={[{ backgroundColor: '#1976d2' }]}
          handleStyle={[
            { borderColor: '#1976d2', backgroundColor: '#fff' },
            { borderColor: '#1976d2', backgroundColor: '#fff' }
          ]}
          railStyle={{ backgroundColor: '#e0e6ed' }}
        />
      </div>
    </div>
  );

  // Render car card
  const renderCarCard = (car) => (
    <div
      className={`cars-card${car.status === 'sold' ? ' sold' : ''}`}
      key={car._id}
      onClick={() => navigate(`/car/${car._id}`)}
    >
      <img
        className="cars-img"
        height="200"
        src={`http://localhost:5000${car.images?.[0] || ''}`}
        alt={car.title}
      />
      <div className="cars-card-content">
        <h3 className="cars-title-small">{car.title}</h3>
        <div className="cars-details">
          {car.brand} {car.model} | {car.year}
        </div>
        <div className="cars-details">
          Registration: {car.registrationNumber}
        </div>
        <div className="cars-details">
          Owner Count: {car.ownerCount}
        </div>
        <div className="cars-price">₹{car.price.toLocaleString()}</div>
        <div className="cars-status-text">{car.status}</div>
      </div>
    </div>
  );

  // Render cars list
  const renderCarsList = () => {
    return (
      <div className="cars-list-grid">
        {filteredCars.map(renderCarCard)}
      </div>
    );
  };

  return (
    <div className="cars-container">
      <h1 className="cars-title">All Cars</h1>
      <div className="cars-grid">
        {renderFilterSidebar()}
        <div className="cars-list">
          {renderCarsList()}
        </div>
      </div>
    </div>
  );
};

export default Cars;
