import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = ({ setUser }) => {
  const navigate = useNavigate();
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        alert('Login successful!');
        navigate('/cars');
      } else {
        const errorData = await response.json();
        alert('Login failed: ' + (errorData.error || (errorData.errors && errorData.errors[0]?.msg) || 'Unknown error'));
      }
    } catch (error) {
      alert('Login failed: ' + error.message);
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <div className="login-title">Welcome Back</div>
        <div style={{ marginBottom: '1rem', color: '#666', textAlign: 'center' }}>
          Please enter your details to sign in
        </div>
        <input
          className="login-input"
          type="email"
          name="email"
          placeholder="Email Address"
          value={formData.email}
          onChange={handleChange}
          required
          autoFocus
        />
        <div style={{ width: '100%', position: 'relative' }}>
          <input
            className="login-input"
            type={showPassword ? 'text' : 'password'}
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
            style={{ paddingRight: '2.5rem' }}
          />
          <button
            type="button"
            onClick={() => setShowPassword((prev) => !prev)}
            style={{
              position: 'absolute',
              right: '10px',
              top: '50%',
              transform: 'translateY(-50%)',
              background: 'none',
              border: 'none',
              cursor: 'pointer',
              color: '#888',
              fontSize: '1rem',
              padding: 0
            }}
            tabIndex={-1}
            aria-label="Toggle password visibility"
          >
            {showPassword ? 'üôà' : 'üëÅÔ∏è'}
          </button>
        </div>
        <button className="login-btn" type="submit">
          Sign In
        </button>
        <div style={{ textAlign: 'center', marginTop: '1.5rem', color: '#666' }}>
          Don't have an account?{' '}
          <button
            type="button"
            style={{ background: 'none', border: 'none', color: '#0a2a66', fontWeight: 600, cursor: 'pointer', padding: 0 }}
            onClick={() => navigate('/signup')}
          >
            Sign up
          </button>
        </div>
      </form>
    </div>
  );
};

export default Login;
