import React from 'react';
import {
  AppBar,
  Toolbar,
  Button,
  Box,
  Snackbar,
  Alert,
  Menu,
  MenuItem,
} from '@mui/material';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../store/slices/authSlice';

const Navbar = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const location = useLocation();

  const isAuthenticated = useSelector((state) => state.auth.isAuthenticated);
  const [open, setOpen] = React.useState(false);
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleLogout = () => {
    dispatch(logout());
    navigate('/login');
  };

  const handleProtectedClick = (e) => {
    if (!isAuthenticated) {
      e.preventDefault();
      setOpen(true);
    }
  };

  return (
    <AppBar position="static" sx={{ backgroundColor: 'white', color: 'black' }} elevation={1}>
      <Toolbar>
        {/* Logo Section */}
        <Box sx={{ flexGrow: 1, display: 'flex', alignItems: 'center' }}>
          <img src="/supermotor-logo.jpg" alt="SuperMotor Logo" style={{ height: 40 }} />
        </Box>

        {/* Navigation Buttons */}
        <Box>
          {isAuthenticated ? (
            <>
              <Button color="inherit" component={Link} to="/cars">Cars</Button>
              <Button color="inherit" component={Link} to="/bookings">Bookings</Button>
              <Button color="inherit" component={Link} to="/wishlist">Wishlist</Button>

              {/* Dropdown Button */}
              <Button
                color="inherit"
                onClick={(event) => setAnchorEl(event.currentTarget)}
              >
                Account â–¾
              </Button>
              <Menu
                anchorEl={anchorEl}
                open={Boolean(anchorEl)}
                onClose={() => setAnchorEl(null)}
              >
                <MenuItem onClick={() => { setAnchorEl(null); navigate('/account'); }}>
                  Profile
                </MenuItem>
                <MenuItem onClick={() => { setAnchorEl(null); navigate('/orders'); }}>
                  My Orders
                </MenuItem>
                <MenuItem onClick={() => { setAnchorEl(null); handleLogout(); }}>
                  Logout
                </MenuItem>
              </Menu>
            </>
          ) : (
            <>
              <Button color="inherit" component={Link} to="/buy" onClick={handleProtectedClick}>Buy</Button>
              <Button color="inherit" component={Link} to="/sell" onClick={handleProtectedClick}>Sell</Button>
              <Button color="inherit" component={Link} to="/login">Login/Signup</Button>
            </>
          )}
        </Box>

        {/* Snackbar for unauthenticated access */}
        <Snackbar
          open={open}
          autoHideDuration={3000}
          onClose={() => setOpen(false)}
          anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
        >
          <Alert onClose={() => setOpen(false)} severity="info" sx={{ width: '100%' }}>
            Please login first to access this feature.
          </Alert>
        </Snackbar>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
