import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './CarDetails.css';

const CarDetails = ({ user }) => {
  const { id } = useParams();
  const [carData, setCarData] = useState(null);
  const [isWishlisted, setIsWishlisted] = useState(false);
  const [selectedImage, setSelectedImage] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);
    fetch(`http://localhost:5000/api/cars/${id}`)
      .then(res => {
        if (!res.ok) {
          throw new Error('Failed to fetch car details');
        }
        return res.json();
      })
      .then(data => {
        setCarData(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching car details:', err);
        setError(err.message);
        setLoading(false);
      });
  }, [id]);

  const handleBookNow = () => {
    alert('Contact 9599942400 to Book Your Test Drive');
  };

  const handleAddToWishlist = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/wishlist', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId: user._id, carId: carData._id })
      });
      if (res.ok) {
        alert('Added to Wishlist');
        setIsWishlisted(true);
      }
    } catch (err) {
      alert('Network error.');
    }
  };

  if (loading) {
    return (
      <div className="car-details-container">
        <div className="loading">Loading car details...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="car-details-container">
        <div className="error">Error: {error}</div>
      </div>
    );
  }

  if (!carData) {
    return (
      <div className="car-details-container">
        <div className="error">Car not found</div>
      </div>
    );
  }

  return (
    <div className="car-details-container">
      <div className="car-details-grid">
        {/* Image Gallery */}
        <div className="car-details-gallery">
          <img
            src={`http://localhost:5000${carData.images[selectedImage]}`}
            alt={carData.name}
            className="car-details-img-main"
          />
          <div className="car-details-thumbs">
            {carData.images && carData.images.length > 0 && carData.images.map((image, index) => (
              <img
                key={index}
                src={`http://localhost:5000${image}`}
                alt={`${carData.name} ${index + 1}`}
                className={`car-details-img-thumb${selectedImage === index ? ' selected' : ''}`}
                onClick={() => setSelectedImage(index)}
              />
            ))}
          </div>
        </div>

        {/* Car Details */}
        <div className="car-details-info">
          <h1 className="car-details-title">{carData.brand}<br/>{carData.model}</h1>
          <h2 className="car-details-price">₹{carData.price.toLocaleString('en-IN')}</h2><br/>
          <div className="car-details-section">
            <div><b>Year:</b> {carData.year}</div>
            <div><b>Kilometers Driven:</b> {carData.kmsDriven} km</div>
            <div><b>Fuel Type:</b> {carData.fuelType}</div>
            <div><b>Transmission:</b> {carData.transmission}</div>
            <div><b>No. of Owner(s):</b> {carData.ownerCount}</div>
            <div><b>Registration:</b> {carData.registrationNumber}</div>
            <div><b>Car Type:</b> {carData.carType}</div>
            <div><b>Location:</b> {carData.location}</div>
            <div><b>Insurance Valid Till:</b> {carData.insuranceValidTill ? new Date(carData.insuranceValidTill).toLocaleDateString() : 'N/A'}</div>
            <div><b>Status:</b> {carData.status}</div>
            <div><b>Car Condition:</b> Good</div>
          </div>
          <p className="car-details-description">{carData.description}</p>
          <button
            className="car-details-btn"
            onClick={handleBookNow}
          >
            Book Now
          </button>
          <button
            className="car-details-btn-outline"
            onClick={handleAddToWishlist}
            disabled={isWishlisted}
          >
            {isWishlisted ? 'Wishlisted' : 'Add to Wishlist'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default CarDetails;
