import React, { useEffect, useState } from 'react';
import { Container, Typography, Grid, Card, CardMedia, CardContent, Box, FormControl, InputLabel, Select, MenuItem, Slider, Paper, Chip } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import './Cars.css';

const Cars = () => {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [brand, setBrand] = useState('');
  const [carType, setCarType] = useState('');
  const [priceRange, setPriceRange] = useState([0, 100000000]);
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(100000000);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/api/cars')
      .then((res) => res.json())
      .then((data) => {
        setCars(data);
        // Set min and max price for slider
        const prices = data.map(car => car.price);
        setMinPrice(Math.min(...prices));
        setMaxPrice(Math.max(...prices));
        setPriceRange([Math.min(...prices), Math.max(...prices)]);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching cars:', err);
        setLoading(false);
      });
  }, []);

  // Get unique brands and car types
  const brands = Array.from(new Set(cars.map(car => car.brand))).filter(Boolean);
  const carTypes = Array.from(new Set(cars.map(car => car.carType))).filter(Boolean);

  // Filter cars
  const filteredCars = cars.filter(car => {
    const matchesBrand = brand ? car.brand === brand : true;
    const matchesType = carType ? car.carType === carType : true;
    const matchesPrice = car.price >= priceRange[0] && car.price <= priceRange[1];
    return matchesBrand && matchesType && matchesPrice;
  });

  if (loading) return <div>Loading...</div>;

  return (
    <Container maxWidth="lg" className="cars-container">
      <Typography variant="h4" gutterBottom align="center">
        All Cars
      </Typography>
      <Grid container spacing={4}>
        {/* Filters Sidebar */}
        <Grid item xs={12} md={3}>
          <Paper className="cars-filter-sidebar" elevation={2}>
            <Typography variant="h6" gutterBottom>Filters</Typography>
            <FormControl className="cars-form-control" size="small" fullWidth>
              <InputLabel>Brand</InputLabel>
              <Select value={brand} label="Brand" onChange={e => setBrand(e.target.value)}>
                <MenuItem value="">All</MenuItem>
                {brands.map(b => <MenuItem key={b} value={b}>{b}</MenuItem>)}
              </Select>
            </FormControl>
            <FormControl className="cars-form-control" size="small" fullWidth>
              <InputLabel>Car Type</InputLabel>
              <Select value={carType} label="Car Type" onChange={e => setCarType(e.target.value)}>
                <MenuItem value="">All</MenuItem>
                {carTypes.map(t => <MenuItem key={t} value={t}>{t}</MenuItem>)}
              </Select>
            </FormControl>
            <Box className="cars-price-range">
              <Typography gutterBottom>Price Range (â‚¹)</Typography>
              <Slider
                value={priceRange}
                min={minPrice}
                max={maxPrice}
                onChange={(_, newValue) => setPriceRange(newValue)}
                valueLabelDisplay="auto"
                step={10000}
              />
            </Box>
          </Paper>
        </Grid>
        {/* Cars Grid */}
        <Grid item xs={12} md={9}>
          <Grid container spacing={4}>
            {filteredCars.map((car) => (
              <Grid item key={car._id} xs={12} sm={6} md={4}>
                <Card className="cars-card" onClick={() => navigate(`/car/${car._id}`)}>
                  <CardMedia
                    component="img"
                    className="cars-img"
                    height="200"
                    image={`http://localhost:5000${car.images && car.images.length > 0 ? car.images[0] : ''}`}
                    alt={car.title}
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h6" component="h2" className="cars-title">{car.title}</Typography>
                    <Typography className="cars-details">{car.brand} {car.model} | {car.year}</Typography>
                    <Typography className="cars-details">Registration: {car.registrationNumber}</Typography>
                    <Typography className="cars-details">Owner Count: {car.ownerCount}</Typography>
                    <Typography variant="h6" color="primary.main" gutterBottom className="cars-price">{car.price}</Typography>
                    <Chip label={car.status} color={car.status === 'sold' ? 'success' : 'default'} className="cars-chip" />
                    <Box className="cars-actions">
                      {/* ...existing button code... */}
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Cars;
