import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';
import './Cars.css';

const Cars = () => {
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [brand, setBrand] = useState('');
  const [carType, setCarType] = useState('');
  const [priceRange, setPriceRange] = useState([0, 100000000]);
  const [minPrice, setMinPrice] = useState(0);
  const [maxPrice, setMaxPrice] = useState(100000000);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/api/cars')
      .then((res) => res.json())
      .then((data) => {
        setCars(data);
        // Set min and max price for slider
        const prices = data.map(car => car.price);
        setMinPrice(Math.min(...prices));
        setMaxPrice(Math.max(...prices));
        setPriceRange([Math.min(...prices), Math.max(...prices)]);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching cars:', err);
        setLoading(false);
      });
  }, []);

  // Get unique brands and car types
  const brands = Array.from(new Set(cars.map(car => car.brand))).filter(Boolean);
  const carTypes = Array.from(new Set(cars.map(car => car.carType))).filter(Boolean);

  // Filter cars
  const filteredCars = cars.filter(car => {
    const matchesBrand = brand ? car.brand === brand : true;
    const matchesType = carType ? car.carType === carType : true;
    const matchesPrice = car.price >= priceRange[0] && car.price <= priceRange[1];
    return matchesBrand && matchesType && matchesPrice;
  });

  return (
    <div className="cars-container">
      <h1 className="cars-title">All Cars</h1>
      <div className="cars-grid">
        {/* Filters Sidebar */}
        <div className="cars-filter-sidebar">
          <h2 className="cars-filter-title">Filters</h2>
          <div className="cars-form-group">
            <label htmlFor="brand-select">Brand</label>
            <select
              id="brand-select"
              value={brand}
              onChange={e => setBrand(e.target.value)}
              className="cars-select"
            >
              <option value="">All</option>
              {brands.map(b => <option key={b} value={b}>{b}</option>)}
            </select>
          </div>
          <div className="cars-form-group">
            <label htmlFor="type-select">Car Type</label>
            <select
              id="type-select"
              value={carType}
              onChange={e => setCarType(e.target.value)}
              className="cars-select"
            >
              <option value="">All</option>
              {carTypes.map(t => <option key={t} value={t}>{t}</option>)}
            </select>
          </div>
          <div className="cars-form-group cars-price-range">
            <label>Price Range (₹)</label>
            <div className="cars-range-labels">
              <span>{Number.isFinite(priceRange[0]) ? priceRange[0].toLocaleString() : ''}</span>
              <span>to</span>
              <span>{Number.isFinite(priceRange[1]) ? priceRange[1].toLocaleString() : ''}</span>
            </div>
            <Slider
              min={minPrice}
              max={maxPrice}
              value={priceRange}
              onChange={setPriceRange}
              step={10000}
              allowCross={false}
              trackStyle={[{ backgroundColor: '#1976d2' }]}
              handleStyle={[
                { borderColor: '#1976d2', backgroundColor: '#fff' },
                { borderColor: '#1976d2', backgroundColor: '#fff' }
              ]}
              railStyle={{ backgroundColor: '#e0e6ed' }}
            />
          </div>
        </div>
        {/* Cars Grid */}
        <div className="cars-list">
          {loading ? (
            <div className="cars-loading">Loading...</div>
          ) : filteredCars.length === 0 ? (
            <div className="cars-empty">No cars found.</div>
          ) : (
            <div className="cars-list-grid">
              {filteredCars.map((car) => (
                <div
                  className={`cars-card${car.status === 'sold' ? ' sold' : ''}`}
                  key={car._id}
                  onClick={() => navigate(`/car/${car._id}`)}
                >
                  <img
                    className="cars-img"
                    height="200"
                    src={`http://localhost:5000${car.images && car.images.length > 0 ? car.images[0] : ''}`}
                    alt={car.title}
                  />
                  <div className="cars-card-content">
                    <h3 className="cars-title-small">{car.title}</h3>
                    <div className="cars-details">{car.brand} {car.model} | {car.year}</div>
                    <div className="cars-details">Registration: {car.registrationNumber}</div>
                    <div className="cars-details">Owner Count: {car.ownerCount}</div>
                    <div className="cars-price">₹{car.price}</div>
                    <div className="cars-status-text">{car.status}</div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
    
  );
};

export default Cars;
