import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Cars.css';

const Cars = () => {
  const navigate = useNavigate();

  const [cars, setCars] = useState([]);
  const [filters, setFilters] = useState({
    brand: '',
    carType: '',
    priceRange: [0, 100000000]
  });
  const [price, setPrice] = useState({
    min: 0,
    max: 100000000
  });

  useEffect(() => {
    const fetchCars = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/cars');
        const data = await response.json();

        setCars(data);

        const prices = data.map(car => car.price);
        const minPrice = Math.min(...prices);
        const maxPrice = Math.max(...prices);

        setPrice({ min: minPrice, max: maxPrice });
        setFilters(prev => ({
          ...prev,
          priceRange: [minPrice, maxPrice]
        }));
      } catch (error) {
        console.error('Error fetching cars:', error);
      }
    };

    fetchCars();
  }, []);

  const brands = [...new Set(cars.map(car => car.brand))].filter(Boolean);
  const carTypes = [...new Set(cars.map(car => car.carType))].filter(Boolean);

  const filteredCars = cars.filter(car => {
    const matchesBrand = !filters.brand || car.brand === filters.brand;
    const matchesType = !filters.carType || car.carType === filters.carType;
    const matchesPrice = car.price >= filters.priceRange[0] && car.price <= filters.priceRange[1];
    return matchesBrand && matchesType && matchesPrice;
  });

  const handleFilterChange = (filterType, value) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: value
    }));
  };

  const handlePriceRangeChange = (newRange) => {
    setFilters(prev => ({
      ...prev,
      priceRange: newRange
    }));
  };

  const formatPrice = (price) => {
    return Number.isFinite(price) ? price.toLocaleString() : '';
  };

  if (!cars.length) return null;
  return (
    <div className="cars-container">
      <h1 className="cars-title">All Cars</h1>
      <div className="cars-grid">

        {/* Filter Sidebar */}
        <div className="cars-filter-sidebar">
          <h2 className="cars-filter-title">Filters</h2>

          {/* Brand Filter */}
          <div className="cars-form-group">
            <label htmlFor="brand-select">Brand</label>
            <select
              value={filters.brand}
              onChange={(e) => handleFilterChange('brand', e.target.value)}
              className="cars-select">
              <option value="">All</option>
              {brands.map(brand => (
                <option key={brand} value={brand}>{brand}</option>
              ))}
            </select>
          </div>

          {/* Car Type Filter */}
          <div className="cars-form-group">
            <label htmlFor="type-select">Car Type</label>
            <select
              id="type-select"
              value={filters.carType}
              onChange={(e) => handleFilterChange('carType', e.target.value)}
              className="cars-select">
              <option value="">All</option>
              {carTypes.map(type => (
                <option key={type} value={type}>{type}</option>
              ))}
            </select>
          </div>

          {/* Price Filter */}
          <div className="cars-form-group cars-price-range">
            <label>Price Range (₹)</label>
            <div className="cars-range-labels">
              <span>{formatPrice(filters.priceRange[0])}</span>
              <span>{formatPrice(filters.priceRange[1])}</span>
            </div>
            <div className="cars-range-inputs">
              <input
                type="range"
                min={price.min}
                max={price.max}
                value={filters.priceRange[0]}
                onChange={(e) => handlePriceRangeChange([Number(e.target.value), filters.priceRange[1]])}
                className="cars-slider"
              />
              <input
                type="range"
                min={price.min}
                max={price.max}
                value={filters.priceRange[1]}
                onChange={(e) => handlePriceRangeChange([filters.priceRange[0], Number(e.target.value)])}
                className="cars-slider"
              />
            </div>
          </div>
        </div>

        {/* Cars List */}
        <div className="cars-list">
          <div className="cars-list-grid">
            {filteredCars.map(car => (
              <div className="cars-card" key={car._id}>
                <img
                  className="cars-img"
                  src={`http://localhost:5000${car.images && car.images.length > 0 ? car.images[0] : ''}`}
                  alt={car.title}
                />
                <div className="cars-card-content">
                  <h2 className="cars-details">{car.brand} {car.model} ({car.year})</h2>
                  <div className="cars-price">₹{car.price.toLocaleString('en-IN')}</div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cars;
