import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Box,
  Paper,
  Grid,
  Chip,
  Button,
  Stepper,
  Step,
  StepLabel,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import ReceiptIcon from '@mui/icons-material/Receipt';
import LocalShippingIcon from '@mui/icons-material/LocalShipping';
import PaymentIcon from '@mui/icons-material/Payment';
import { useSelector } from 'react-redux';

const Orders = () => {
  const navigate = useNavigate();
  const user = useSelector((state) => state.auth.user);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user) {
      fetch(`http://localhost:5000/api/orders/user/${user._id}`)
        .then(res => res.json())
        .then(data => {
          // Only keep orders where car.status === 'sold'
          setOrders(data.filter(order => order.carId && order.carId.status === 'sold'));
          setLoading(false);
        })
        .catch(err => {
          console.error('Error fetching orders:', err);
          setLoading(false);
        });
    }
  }, [user]);

  const getStatusColor = (status) => {
    switch (status) {
      case 'Processing':
        return 'warning';
      case 'Confirmed':
        return 'info';
      case 'Delivered':
        return 'success';
      case 'sold':
        return 'success';
      default:
        return 'default';
    }
  };

  const orderSteps = ['Order Placed', 'Processing', 'Delivered'];

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom>
        My Orders
      </Typography>

      <Grid container spacing={3}>
        {loading ? (
          <Grid item xs={12}><Typography variant="body1">Loading...</Typography></Grid>
        ) : orders.length > 0 ? (
          orders.map((order) => (
            <Grid item xs={12} key={order._id}>
              <Paper
                elevation={2}
                sx={{
                  p: 3,
                  borderRadius: 2,
                  '&:hover': { transform: 'translateY(-2px)', transition: 'transform 0.2s' },
                }}
              >
                <Grid container spacing={3}>
                  <Grid item xs={12} sm={3}>
                    <img
                      src={order.carId.images && order.carId.images[0] ? `http://localhost:5000${order.carId.images[0]}` : ''}
                      alt={order.carId.name}
                      style={{
                        width: '100%',
                        height: '120px',
                        objectFit: 'cover',
                        borderRadius: '8px',
                      }}
                    />
                  </Grid>

                  <Grid item xs={12} sm={6}>
                    <Typography variant="h6" gutterBottom>
                      {order.carId.name || order.carId.title}
                    </Typography>

                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 1 }}>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <ReceiptIcon color="action" fontSize="small" />
                        <Typography variant="body2" color="text.secondary">
                          Order #{order.orderNumber}
                        </Typography>
                      </Box>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <LocalShippingIcon color="action" fontSize="small" />
                        <Typography variant="body2" color="text.secondary">
                          {order.orderDate}
                        </Typography>
                      </Box>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <PaymentIcon color="action" fontSize="small" />
                        <Typography variant="body2" color="text.secondary">
                          {order.paymentStatus}
                        </Typography>
                      </Box>
                    </Box>

                    <Stepper activeStep={order.currentStep} alternativeLabel size="small">
                      {orderSteps.map((label) => (
                        <Step key={label}>
                          <StepLabel>{label}</StepLabel>
                        </Step>
                      ))}
                    </Stepper>
                  </Grid>

                  <Grid item xs={12} sm={3} sx={{ textAlign: 'right' }}>
                    <Typography variant="h6" color="primary.main" gutterBottom>
                      {order.carId.price}
                    </Typography>

                    <Chip
                      label={order.carId.status}
                      color={getStatusColor(order.carId.status)}
                      sx={{ mb: 2 }}
                    />

                    <Box>
                      <Button
                        variant="outlined"
                        size="small"
                        onClick={() => navigate(`/car/${order.carId._id}`)}
                        sx={{ mr: 1 }}
                      >
                        View Car
                      </Button>
                      <Button
                        variant="contained"
                        size="small"
                        color="primary"
                        onClick={() => console.log('Track order:', order._id)}
                      >
                        Track Order
                      </Button>
                    </Box>
                  </Grid>
                </Grid>
              </Paper>
            </Grid>
          ))
        ) : (
          <Grid item xs={12}>
            <Box sx={{ textAlign: 'center', py: 8 }}>
              <Typography variant="h6" color="text.secondary" gutterBottom>
                No orders found
              </Typography>
              <Button
                variant="contained"
                onClick={() => navigate('/')}
                sx={{ mt: 2 }}
              >
                Browse Cars
              </Button>
            </Box>
          </Grid>
        )}
      </Grid>
    </Container>
  );
};

export default Orders;
